const { test, expect } = require('@playwright/test');
const { HomePage } = require('./HomePage');

test('Verification of searching', async ({ page }) => {
    const homePage = new HomePage(page);  // Create a new object

    await homePage.navigate();  // Open the page
    await homePage.searchForProperty('New York, NY');  // Search property

    await expect(page).toHaveURL(/search/);
});